import React,{useState,useEffect} from 'react';
// import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import './Home.css'
// import {AiFillLike} from 'react-icons/ai'

const Products=()=>{
  const product=[];
  const employeeID=React.createRef();
  const offerId=React.createRef();
  const status=React.createRef();
  const Likes=React.createRef();
  const Category=React.createRef();
  const OpenedDate=React.createRef();
  const EngagedDate=React.createRef();
  const ClosedDate=React.createRef();
  const details=React.createRef();
  const [getResult, setGetResult] = useState(null);
  async function OnSubmit() {
  const EmployeeID = employeeID.current.value;
  const OfferId = offerId.current.value;
  await fetch(`hhttp://localhost:5001//api/Offer/GetOffersList/{Offerid}`)
  .then(response => response.json())
        .then((result) => {
          product.push(result);
          console.log(product);
        },
        (error) => {
            alert("Some Error Occured During Fetching");
        }
    )
};
  
return (
<div>                
  {   product &&
          <div>
              <h4 align="center">Business Property List</h4>
              <table className="table table-striped" style={{ marginTop: 10, border: 2 }}>
                  <thead>
                      <tr>
                          <th>ConsumerId</th>
                          <th>PropertyId</th>
                          <th>BuildingSqft</th>
                          <th>BuildingType</th>
                          <th>BuildingStoreys</th>
                          <th>BuildingAge</th>
                          <th>CostOfTheAsset</th>
                          <th>SalvageValue</th>
                          <th>UsefulLifeOfTheAsset</th>
                          <th>PropertyValue</th>
                      </tr>
                  </thead>
                  <tbody>
                      {product.map(bp =>
                          <tr key={bp.OfferId}>
                              <td>{bp.propertyId}</td>
                              <td>{bp.status}</td>
                              <td>{bp.Likes}</td>
                              <td>{bp.Category}</td>
                              <td>{bp.OpenedDate}</td>
                              <td>{bp.EngagedDate}</td>
                              <td>{bp.ClosedDate}</td>
                              <td>{bp.details}</td>
                              {/* <td>{bp.usefulLifeOfTheAsset}</td>
                              <td>{bp.propertyValue}</td> */}
                          </tr>
                      )}
                  </tbody>
              </table>
          </div>
  } 
  </div> 
)
}
