import React, { Component } from 'react'
import {AiFillLike} from 'react-icons/ai'

export default class Offers extends Component {
    constructor(props){
        super(props);
        this.state={ofs:[]};
    }
    refreshList(){
        fetch('https://localhost:5001/api/Offer/GetOffersList')
        .then(response=>response.json())
        .then(data=>{
            this.setState({ofs:data});
        });
    }
    componentDidMount(){
        this.refreshList();
    }
    componentDidUpdate(){
        this.refreshList();

    }
  render() {
    const {ofs}=this.state;

    return (
        <>
        {ofs.map(offer=>

        <div className=' box col-lg-3 col-sm-6 col-md-3 mb-4'>
        <div class="card h-100 text-center p-4" key={offer.OfferId}>
          <img src={"img.png"} class="card-img-top" alt={offer.Category} height="300px" />
          <div class="card-body">
              <h5 class="card-title mb-0">{offer.Details.substring(0,12)}</h5>
              <p class="card-text">{offer.Status}</p>
              <i className="like"><AiFillLike/>{offer.Likes}</i>
              <a  href="#" class="btn btn-primary">View Details</a>
          </div>
         </div>
      </div>
        
    )}
    </>
    )
  }
}
